def on_on_update2():
    scene.center_camera_at(spider.x, spider.y)
    spider.vx = 110
    if IsOnWall == 0:
        if gra == 0:
            spider.vy = 130
        else:
            spider.vy = -150
    else:
        if gra == 0:
            spider.vy = 9999
        else:
            spider.vy = -9999
game.on_update(on_on_update2)

